{
    var context = new ScenarioContext("X", d => d.Invocation());
    var invoked = false;
    // The shared fact is never reached
    if ((bool)(object)false)
    {
        context.SharedFact("Y", () => invoked = true);
    }

    // A dummy target
    context.Fact("X", () =>
    {
    });
    Assert.False(invoked);
}

{
