    Assert.Equal(0, activityId);
    activity.Id = 0;
    var timeSegment = new TimeSegment();
    var timeSegmentId = timeSegment.Id;
    Assert.Equal(0, timeSegmentId);
    timeSegment.Id = 0;
    activityId = timeSegment.ActivityId;
    Assert.Equal(0, activityId);
